package com.spray.stock.adaptersimport android.content.Contextimport android.util.Logimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.spray.stock.Rimport com.spray.stock.models.item.Reportimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass ReportAdapter @Inject constructor(val context: Context) : RecyclerView.Adapter<ReportAdapter.ViewHolder>() {    private lateinit var mView: View    private var mReports = mutableListOf<Report>()    private val diffCallback = object : DiffUtil.ItemCallback<Report>() {        override fun areItemsTheSame(oldItem: Report, newItem: Report) =            oldItem.id == newItem.id        override fun areContentsTheSame(oldItem: Report, newItem: Report) =            oldItem == newItem    }    private val mAsyncDiff = AsyncListDiffer(this, diffCallback)    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ReportAdapter.ViewHolder {        mView = LayoutInflater.from(parent.context).inflate(R.layout.items_report_stock, parent, false)        return ViewHolder(mView)    }    override fun getItemCount(): Int = mAsyncDiff.currentList.size    override fun onBindViewHolder(holder: ReportAdapter.ViewHolder, position: Int) {        holder.bind(mAsyncDiff.currentList[position])    }    fun submitList(reports: MutableList<Report>?) {        mAsyncDiff.submitList(reports)        mReports.addAll(reports!!)    }    fun clear() {        mReports.clear()        mAsyncDiff.submitList(mReports)    }    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        private val title: TextView = itemView.findViewById(R.id.tv_report_stock_title)        private val name: TextView = itemView.findViewById(R.id.tv_report_stock_name)        fun bind(report: Report) {            Log.d("ReportAdapter", report.title)            title.text = report.title            name.text = report.title        }    }}