package com.spray.stock.adaptersimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.lifecycle.Lifecycleimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.spray.stock.views.fragments.tabs.InvestFragmentimport com.spray.stock.views.fragments.tabs.StocksFragmentimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass ReportViewPagerAdapter @Inject constructor(supportFragmentManager: FragmentManager, lifecycle: Lifecycle):    FragmentStateAdapter(supportFragmentManager, lifecycle) {    private val mFragments = mutableListOf<Fragment>()    private val mFragmentTitles = mutableListOf<String>()    override fun getItemCount(): Int = 2    override fun createFragment(position: Int): Fragment {        return when (position) {                0 -> {                    StocksFragment()                }                1 -> {                    InvestFragment()                }                else -> {                    Fragment()                }            }    }////    override fun getCount(): Int = mFragments.size////    override fun getItem(position: Int): Fragment = mFragments[position]////    override fun getPageTitle(position: Int): CharSequence = mFragmentTitles[position]    fun addFragment(fragment: Fragment, title: String) {        mFragments.add(fragment)        mFragmentTitles.add(title)    }}