package com.spray.stock.apiimport com.spray.stock.models.ApiPageResponseimport com.spray.stock.models.item.RecommendedItemCommentimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Pathimport retrofit2.http.Queryinterface RecommendedItemCommentApi {    @GET("api/v1/recommendedItems/{id}/comments")    suspend fun getRecommendedItemComments(        @Path("id") id: Long,        @Query("page") page: Int,        @Query("size") size: Int    ): Response<ApiPageResponse<RecommendedItemComment>>}