package com.spray.stock.apiimport com.spray.stock.models.ApiPageResponseimport com.spray.stock.models.item.Reportimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Pathimport retrofit2.http.Queryinterface ReportApi {    @GET("api/v1/reports")    suspend fun getReports(        @Query("type") type: String,        @Query("tag") tag: String,        @Query("page") page: Int,        @Query("size") size: Int    ): Response<ApiPageResponse<Report>>    @GET("api/v1/reports/{id}")    suspend fun getReports(        @Path("id") id: Long    ): Response<Report>}