package com.spray.stock.dialogsimport android.app.Dialogimport android.content.Contextimport android.content.Context.INPUT_METHOD_SERVICEimport android.graphics.Colorimport android.graphics.drawable.ColorDrawableimport android.os.Bundleimport android.util.Logimport android.view.Viewimport android.view.inputmethod.InputMethodManagerimport com.spray.stock.databinding.DialogCommentBindingclass CommentDialogImpl(context: Context, commentDialog: CommentDialog): Dialog(context), View.OnClickListener {    private var commentDialog: CommentDialog? = null    private var mBinding: DialogCommentBinding? = null    init {        this.commentDialog = commentDialog    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        mBinding = DialogCommentBinding.inflate(layoutInflater)        mBinding?.root?.let { setContentView(it) }        window!!.setBackgroundDrawable(ColorDrawable(Color.TRANSPARENT))        mBinding?.btnCommentWrite?.requestFocus()////        val manager: InputMethodManager? = context.getSystemService(INPUT_METHOD_SERVICE) as InputMethodManager?//        manager?.toggleSoftInput(InputMethodManager.SHOW_FORCED, 0)//        mBinding?.btnCommentWrite?.setOnClickListener(this)    }    override fun onClick(v: View?) {        Log.d("로그", "코멘트 등록 버튼 클릭")        this.commentDialog?.onCommentWriteClicked()    }//    override fun cancel() {//        val imm = context.getSystemService(Context.INPUT_METHOD_SERVICE) as InputMethodManager//        imm.hideSoftInputFromWindow(this.window?.currentFocus?.windowToken, 0)//        super.cancel()//    }}