package com.spray.stock.viewModels.itemsimport androidx.lifecycle.ViewModelimport androidx.lifecycle.liveDataimport com.spray.stock.BuildConfigimport com.spray.stock.api.ReportApiimport com.spray.stock.config.RetrofitClientimport com.spray.stock.viewModels.Resourceimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.Dispatchersimport javax.inject.Inject@HiltViewModelclass ReportViewModel @Inject constructor() : ViewModel() {    fun loadReports(type: String, tag: String, page: Int) = liveData(Dispatchers.IO) {        emit(Resource.loading())        val api = RetrofitClient.get(BuildConfig.ITEM_BASE_URL)!!.create(ReportApi::class.java)        val response = api.getReports(type, tag, page, 20)        emit(Resource.success(response))    }}