package com.spray.stock.views.fragments.dialogimport android.graphics.Pointimport android.os.Bundleimport android.util.Logimport android.view.*import androidx.fragment.app.DialogFragmentimport com.spray.stock.Rimport dagger.hilt.android.AndroidEntryPoint@AndroidEntryPointclass CommentActionFragment : DialogFragment() {    override fun getTheme(): Int = R.style.ThemeOverlay_MaterialComponents_Dialog    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {        return inflater.inflate(R.layout.fragment_comment_action, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        val request = arguments?.getString("id")        Log.d("REQUEST DATA", request.toString())    }    /**     * 디바이스 size (x, y) 에 따라 fit 하게 DialogFragment 띄워준다.     */    override fun onResume() {        super.onResume()        val display = context?.display        val size = Point()        display?.getRealSize(size)        val params: ViewGroup.LayoutParams? = dialog?.window?.attributes        val deviceWidth = size.x        params?.width = (deviceWidth * 0.9).toInt()        dialog?.window?.attributes = params as WindowManager.LayoutParams    }}